mapper.select:
SELECT * FROM eventCreditRules
where rank rank IS NOT NULL

	AND eventTypeId = #{eventTypeId}


	AND seriesId = #{seriesId}


	AND name = #{name}


	AND stage = #{stage}

SELECT * FROM eventCreditRules
where rank IS NULL

	AND eventTypeId = #{eventTypeId}


	AND seriesId = #{seriesId}


	AND name = #{name}


	AND stage = #{stage}


	AND searchNonrankingRules = #{searchNonrankingRules}
