mapper.select:
SELECT users.likes as likes, users.id as userId, users.name as userName, mobileNum, location, roleId, siteId, status, credit, goldenMedal, silverMedal, bronzeMedal, users.createdTime as userCreatedTime, users.lastModifiedTime as userLastModifiedTime, kids.id as kidId, kids.name as kidName, schoolType, schoolName, kids.createdTime as userCreatedTime, kids.lastModifiedTime as userLastModifiedTime FROM users LEFT JOIN kids ON users.id = kids.userId
AND users.mobileNum = #{mobileNum}
AND users.wcId = #{wcId}
AND users.id in (SELECT userId FROM kids WHERE kids.name = #{kidName})

mapper.update:
update users
set
password=#{password},
idp=#{idp}
where
mobileNum=#{mobileNum};
