function messageLike() {
	if ($(".messagesList")) {
		$(".messagesList").on("click", ".star", function() {
			$(this).removeClass("star");
			$(this).addClass("dislikes")
		});
		$(".messagesList").on("click", ".dislikes", function() {
			$(this).removeClass("dislikes");
			$(this).addClass("star")
		})
	}
}
function tempStats() {
	if ($(".tempStat")) {
		$(".tempStat").each(function() {
			var a = Math.floor(Math.random() * (1 + 120));
			$(this).html(a + "Â°");
			if (a < 20) {
				$(this).animate({
					borderColor : "#67c2ef"
				}, "fast")
			} else {
				if (a > 19 && a < 40) {
					$(this).animate({
						borderColor : "#CBE968"
					}, "slow")
				} else {
					if (a > 39 && a < 60) {
						$(this).animate({
							borderColor : "#eae874"
						}, "slow")
					} else {
						if (a > 59 && a < 80) {
							$(this).animate({
								borderColor : "#fabb3d"
							}, "slow")
						} else {
							if (a > 79 && a < 100) {
								$(this).animate({
									borderColor : "#fa603d"
								}, "slow")
							} else {
								$(this).animate({
									borderColor : "#ff5454"
								}, "slow")
							}
						}
					}
				}
			}
		})
	}
}

function numberWithCommas(a) {
	a = a.toString();
	var b = /(-?\d+)(\d{3})/;
	while (b.test(a)) {
		a = a.replace(b, "$1.$2")
	}
	return a
}
